"use strict";
const {resolve} = require('path');
const fs = require('fs');
const dotenv = require("dotenv");

const config = () =>{
    const rootPath = "../../../../.env";
    const clusterPath = '/configmap/.env';
    let envPath = resolve(__dirname, rootPath);
    if (!fs.existsSync(envPath))envPath = clusterPath;    
    dotenv.config({ path: envPath });
}

module.exports ={
    config
}